import Header from './components/layout/Header.vue';
import Todos from './components/Todos.vue';
import AddTodo from './components/AddTodo.vue';
import SearchTodo from './components/SearchTodo.vue';
import axios from 'axios';
export default {
  name: 'App',
  components: {
    Header,
    Todos,
    AddTodo,
    SearchTodo
  },

  data() {
    return {
      todos: []
    };
  },

  methods: {
    deleteTodo(id) {
      this.todos = this.todos.filter(todo => todo.id !== id);
    },

    addTodo(newTodo) {
      const {
        title,
        completed
      } = newTodo;
      axios.post('https://jsonplaceholder.typicode.com/todos', {
        title,
        completed
      }).then(res => this.todos = [...this.todos, res.data]).catch(err => console.log(err));
    }

  },

  created() {
    axios.get('https://jsonplaceholder.typicode.com/todos?_limit=5').then(res => this.todos = res.data).catch(err => console.log(err));
  },

  computed: {
    filteredTodo: function () {
      return this.todos.filter(todo => {
        return todo.title.match(this.search);
      });
    }
  }
};
